dev/Singleton-Pattern-for-Connecting-DB-.content:
More info: www.codingexplorations.com/blog/implementing-the-singleton-pattern-in-go
var instance *sql.DB
var once sync.Once

func GetDB() *sql.DB {
    once.Do(func() {
        db, err := sql.Open(&quot;postgres&quot;, &quot;connection string&quot;)
        if err != nil {
            log.Fatalf(&quot;Failed to connect: %v&quot;, err)
        }
        instance = db
    })
    return instance
}
